# -*- coding: utf-8 -*-nick = []pas = []name = []while True:    a = input()    if a == '':        break    else:        b = a.split(':')        nick.append(b[0])        pas.append(b[1])        name.append(b[4])bad_pass = input().split(';')for i in range(len(pas)):    for j in range(len(bad_pass)):        if pas[i] == bad_pass[j]:            print('To:', nick[i])            c = name[i]            d = c.split(',')            g = d[0]            print(g + ',', 'ваш пароль слишком простой, смените его.')