# -*- coding: utf-8 -*-def print_long_words(text):    text = text.lower()    for i in text:        if i not in ' абвгдеёжзийклмнопрстуфхцчшщъыьэюяabcdefghijklmnopqrstuvwxyz':            text = text.replace(i, ' ')    bukvi = 'аоэиуыеёюяaeiouy'    text = text.split(' ')    for word in text:        count = 0        word1 = list(word)        for bukva in word1:            if bukva in bukvi:                count += 1        if count >= 4:            if '.' in word or ',' in word or '!' in word or '?' in word:                word = word[:-1]            print(word.lower())