# -*- coding: utf-8 -*-def prost(aa):    count = 0    aa = int(aa)    for i in range(1, aa + 1):        if aa % i == 0:            count += 1    if count == 2:        return 1    else:        return 0def pal(bb):    rev = list(bb)    rev.reverse()    q = ''    for i in rev:        q += i    if bb == q:        return 1    else:        return 0def step(cc):    count = 0    for i in range(1, 1000):        stepp = 2 ** i        if str(stepp) == cc:            count = 1            break    if count == 1:        return 1    else:        return 0def check_pin(code):    code = code.split('-')    a = prost(code[0])    b = pal(code[1])    c = step(code[2])    if a == 1 and b == 1 and c == 1:        return 'Корректен'    else:        return 'Некорректен'