# -*- coding: utf-8 -*-names = ''date = ''def setup_profile(name, vacation_dates):    global names    global date    names = name    date = vacation_datesdef print_application_for_leave():    print('Заявление на отпуск в период', date + '.', names)    def print_holiday_money_claim(amount):    print('Прошу выплатить', amount, 'отпускных денег.', names)def print_attorney_letter(to_whom):    print('На время отпуска в период', date, 'моим заместителем назначается', to_whom + '.', names)    