# -*- coding: utf-8 -*-d = {}d_n = {}def get_transactions(t):    global d    global d_n    if t != 'print_it':        t = t.split('-')[1]        trans, summ = t.split(':')[0], t.split(':')[1]        if trans in d:            deng = d[trans]            deng += int(summ)            d[trans] = deng        else:            deng = int(summ)            d[trans] = deng        if trans in d_n:            kol = d_n[trans]            kol += 1            d_n[trans] = kol        else:            d_n[trans] = 1    else:        for i in d:            print(d_n[i], i, d[i])