# -*- coding: utf-8 -*-items = []count = 0num = 1flag = Falsedef add_item(name, cena):    global count    global flag    items.append(name + ' - ' + str(cena))    count += cena    flag = Truedef print_receipt():    global num    global count    global flag    global items    if flag:        print('Чек', str(num) + '. Всего предметов:', len(items))        for i in items:            print(i)        print('Итого:', count)        num += 1        print('-----')        flag = False        items = []        count = 0