import mathdef larger_root(b, c):    d = b ** 2 - (4 * c)    if d > 0:        x1 = (-b - math.sqrt(d)) / 2        x2 = (-b + math.sqrt(d)) / 2    else:        x = -b / 2        x1 = float(x)        x2 = int(x)    if x1 > x2:        return x1    else:        return x2def smaller_root(b, c):    d = b ** 2 - (4 * c)    if d > 0:        x1 = (-b - math.sqrt(d)) / 2        x2 = (-b + math.sqrt(d)) / 2    else:        x = -b / 2        x1 = float(x)        x2 = int(x)    if x1 > x2:        return x2    else:        return x1def discriminant(a, b, c):    d = b ** 2 - 4 * a * c    return ddef main():    global b    global c    b = input()    if '.' in b:        b = float(b)    else:        b = int(b)    c = input()    if '.' in c:        c = float(c)    else:        c = int(c)    d = b ** 2 - 4 * c    print(d)    print(smaller_root(b, c), end=' ')    print(larger_root(b, c))