# -*- coding: utf-8 -*-from sys import stdin as stimport pymorphy2 as morpmorph = morp.MorphAnalyzer()text = st.read().lower().split()for w in text:    parse = morph.parse(w)[0]    if 'NOUN' in parse.tag.POS:        if 'sing' in parse.tag.number:            if parse.tag.gender == 'masc':                if parse.tag.animacy == 'anim':                    print('Живой')                else:                    print('Не живой')            elif parse.tag.gender == 'femn':                if parse.tag.animacy == 'anim':                    print('Живая')                else:                    print('Не живая')            elif parse.tag.gender == 'neut':                if parse.tag.animacy == 'anim':                    print('Живое')                else:                    print('Не живое')        else:            if parse.tag.animacy == 'anim':                print('Живые')            else:                print('Не живые')    else:        print('Не существительное')