# -*- coding: utf-8 -*-from PIL import ImageDrawfrom PIL import Imagefrom math import sinfrom math import cosfrom math import piclass MyImageDraw(ImageDraw.ImageDraw):    def regular_polygon(self, center_coords, sides, radius, rotation=0, fill=None, outline=None):        x, y = center_coords        coords = []        for i in range(1, sides + 1):            coords.append((x + radius * cos(rotation + 2 * pi * i / sides), (y + radius * sin(rotation + 2 * pi * i / sides))))        self.polygon(coords, fill=fill, outline=outline)