m = int(input())n = int(input())k = int(input())people = set()pause = set()flag = 0for i in range(m + n + k):    name = input()    if name in people:        flag += 1        pause.add(name)    people.add(name)if (n + m + k) and len(people) == n:    print('NO')else:    if len(pause) + flag > 0:        if (len(pause) + flag) % 2 != 0:            print((len(pause) + flag) % 2)        else:            print((len(pause) + flag) // 2)    else:        print('NO')